delimiter ;;
CREATE EVENT `update_post_status`
ON SCHEDULE
EVERY '1' MINUTE STARTS '2024-12-21 02:31:58'
DO UPDATE news
    SET Status = 3
    WHERE Status = 2 AND PublishDate <= NOW()
;;
delimiter ;

