DROP TABLE IF EXISTS `newstag`;
CREATE TABLE `newstag`  (
  `NewsID` int NOT NULL,
  `TagID` int NOT NULL,
  PRIMARY KEY (`NewsID`, `TagID`) USING BTREE,
  INDEX `TagID`(`TagID` ASC) USING BTREE,
  CONSTRAINT `newstag_ibfk_1` FOREIGN KEY (`NewsID`) REFERENCES `news` (`NewsID`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `newstag_ibfk_2` FOREIGN KEY (`TagID`) REFERENCES `tag` (`TagID`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = DYNAMIC;

