-- ----------------------------
-- Event structure for DeleteExpiredOTP
-- ----------------------------
DROP EVENT IF EXISTS `DeleteExpiredOTP`;
delimiter ;;
CREATE EVENT `DeleteExpiredOTP`
ON SCHEDULE
EVERY '1' HOUR STARTS '2024-12-20 16:43:10'
DO UPDATE account
SET OTP = NULL, OTP_CreatedAt = NULL
WHERE OTP_CreatedAt < NOW() - INTERVAL 3 HOUR
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
